import React, { useState, useEffect, useRef } from 'react';

function DetectDefectsStep({ projectId, normalModelData, onStepComplete }) {
  const [detectionState, setDetectionState] = useState({
    selectedFiles: [],
    uploadedImages: [],
    uploading: false,
    extractingROI: false,
    detectingAnomalies: false,
    roiResults: null,
    anomalyResults: null,
    method: 'mahalanobis',
    thresholdPercentile: 95.0
  });
  const [errors, setErrors] = useState({});

  // Monitor state changes
  useEffect(() => {
    console.log(`🔄 State changed: ${detectionState.selectedFiles.length} files selected, ${detectionState.uploadedImages.length} uploaded`);
  }, [detectionState.selectedFiles, detectionState.uploadedImages]);

  const handleFileSelection = (event) => {
    const files = Array.from(event.target.files);
    console.log(`📁 Selected ${files.length} defective images:`, files.map(f => f.name));

    // Store selected files (not uploaded yet)
    setDetectionState(prev => ({
      ...prev,
      selectedFiles: files,
      uploadedImages: [] // Clear uploaded images when new files are selected
    }));
    setErrors({});
  };

  const handleSettingChange = (setting, value) => {
    setDetectionState(prev => ({
      ...prev,
      [setting]: value
    }));
  };

  const uploadDefectiveImages = async () => {
    if (detectionState.selectedFiles.length === 0) {
      setErrors({ upload: 'Please select defective images first' });
      return;
    }

    console.log(`🚀 Uploading ${detectionState.selectedFiles.length} defective images to server`);
    setDetectionState(prev => ({ ...prev, uploading: true }));
    setErrors({});

    try {
      const formData = new FormData();
      detectionState.selectedFiles.forEach((file) => {
        formData.append('defective_images', file);
      });

      console.log(`🌐 Uploading to: /api/auto-annotation/projects/${projectId}/upload-defective-images`);

      const response = await fetch(`/api/auto-annotation/projects/${projectId}/upload-defective-images`, {
        method: 'POST',
        body: formData
      });

      const result = await response.json();
      console.log('📡 Upload response:', result);

      if (result.status === 'success') {
        setDetectionState(prev => ({
          ...prev,
          uploadedImages: result.uploaded_files,
          selectedFiles: [] // Clear selected files after upload
        }));
        console.log(`✅ Successfully uploaded ${result.uploaded_count} defective images`);
      } else {
        setErrors({ upload: result.message || 'Upload failed' });
      }
    } catch (error) {
      console.error('❌ Upload failed:', error);
      setErrors({ upload: `Upload error: ${error.message}` });
    } finally {
      setDetectionState(prev => ({ ...prev, uploading: false }));
    }
  };

  const extractROIFromDefectiveImages = async () => {
    if (detectionState.uploadedImages.length === 0) {
      setErrors({ roi: 'Please upload defective images first' });
      return;
    }

    console.log(`🔍 Starting ROI extraction for ${detectionState.uploadedImages.length} uploaded defective images`);
    setDetectionState(prev => ({ ...prev, extractingROI: true }));
    setErrors({});

    try {
      const formData = new FormData();
      formData.append('component_description', 'metal plate');
      formData.append('confidence_threshold', 0.3);

      console.log(`🚀 Sending request to extract ROI from uploaded defective images`);
      console.log(`🌐 Request URL: /api/auto-annotation/projects/${projectId}/extract-defective-roi`);

      const response = await fetch(`/api/auto-annotation/projects/${projectId}/extract-defective-roi`, {
        method: 'POST',
        body: formData
      });

      console.log('📡 Response received, status:', response.status);

      if (!response.ok) {
        const errorText = await response.text();
        console.log('❌ Response not OK, error text:', errorText);
        throw new Error(`HTTP ${response.status}: ${errorText}`);
      }

      const result = await response.json();
      console.log('📡 Response JSON:', result);

      if (result.status === 'success') {
        setDetectionState(prev => ({
          ...prev,
          roiResults: result
        }));
        console.log('✅ ROI extraction completed:', result);
      } else {
        console.log('❌ API returned error status:', result);
        setErrors({ roi: result.message || 'ROI extraction failed' });
      }
    } catch (error) {
      console.error('❌ ROI extraction failed with error:', error);
      setErrors({ roi: `Network error: ${error.message}` });
    } finally {
      setDetectionState(prev => ({ ...prev, extractingROI: false }));
    }
  };

  const detectAnomalies = async () => {
    if (!detectionState.roiResults) {
      setErrors({ anomaly: 'Please extract ROI from defective images first' });
      return;
    }

    setDetectionState(prev => ({ ...prev, detectingAnomalies: true }));
    setErrors({});

    try {
      const formData = new FormData();
      formData.append('method', detectionState.method);
      formData.append('threshold_percentile', detectionState.thresholdPercentile);

      console.log(`🧠 Running anomaly detection on defective ROI images`);

      const response = await fetch(`/api/auto-annotation/projects/${projectId}/detect-defects`, {
        method: 'POST',
        body: formData
      });

      const result = await response.json();

      if (result.status === 'success') {
        setDetectionState(prev => ({
          ...prev,
          anomalyResults: result
        }));
        console.log('✅ Anomaly detection completed:', result);
      } else {
        setErrors({ anomaly: result.message || 'Anomaly detection failed' });
      }
    } catch (error) {
      console.error('Anomaly detection failed:', error);
      setErrors({ anomaly: 'Network error. Please try again.' });
    } finally {
      setDetectionState(prev => ({ ...prev, detectingAnomalies: false }));
    }
  };

  const handleProceedToNextStep = () => {
    if (onStepComplete) {
      onStepComplete(detectionState.anomalyResults);
    }
  };

  const getAnomalyCount = () => {
    if (!detectionState.anomalyResults) return 0;
    return detectionState.anomalyResults.results.filter(r => 
      r.image_scores.anomaly_percentage > 5.0
    ).length;
  };

  return (
    <div className="detect-defects-step">
      <div className="step-header">
        <h4>🎯 Step 3: Detect Defects</h4>
        <p>Upload defective images and detect anomalous regions using your normal model from Stage 2.</p>
      </div>

      {/* File Upload Section */}
      <div className="defective-images-upload">
        <h5>📸 Upload Defective Images</h5>
        <div className="upload-info">
          <p>Upload images that may contain defects. These should be <strong>different</strong> from the normal images used in Stages 1-2.</p>
        </div>

        <div className="file-upload-area">
          <input
            type="file"
            id="defective-images"
            multiple
            accept="image/*"
            onChange={handleFileSelection}
            className="file-input"
            disabled={detectionState.uploading || detectionState.extractingROI || detectionState.detectingAnomalies}
          />
          <label htmlFor="defective-images" className="file-upload-label">
            <span className="upload-icon">📁</span>
            <span className="upload-text">
              {detectionState.selectedFiles.length === 0 
                ? 'Choose defective images...' 
                : `${detectionState.selectedFiles.length} images selected`
              }
            </span>
          </label>
        </div>

        {detectionState.selectedFiles.length > 0 && (
          <div className="upload-actions">
            <button
              className="upload-button"
              onClick={uploadDefectiveImages}
              disabled={detectionState.uploading || detectionState.extractingROI || detectionState.detectingAnomalies}
              style={{
                padding: '10px 20px',
                background: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: detectionState.uploading ? 'wait' : 'pointer',
                marginTop: '10px'
              }}
            >
              {detectionState.uploading 
                ? `🔄 Uploading ${detectionState.selectedFiles.length} images...` 
                : `📤 Upload ${detectionState.selectedFiles.length} defective images`
              }
            </button>
            <p style={{ fontSize: '12px', color: '#666', marginTop: '5px' }}>
              Click to upload selected images to the server before extracting ROI
            </p>
          </div>
        )}

        {detectionState.uploadedImages.length > 0 && (
          <div className="upload-summary">
            <p>✅ {detectionState.uploadedImages.length} defective images uploaded to server</p>
          </div>
        )}

        {errors.upload && (
          <div className="error-banner">
            ❌ {errors.upload}
          </div>
        )}
      </div>

      {/* ROI Extraction Section */}
      <div className="roi-extraction-section">
        <h5>🔍 Extract ROI from Defective Images</h5>
        <p>First, we need to extract the component regions from your defective images.</p>

        {errors.roi && (
          <div className="error-banner">
            ❌ {errors.roi}
          </div>
        )}

        <button
          className="extract-roi-button"
          onClick={extractROIFromDefectiveImages}
          disabled={detectionState.extractingROI || detectionState.defectiveImages.length === 0}
          style={{
            opacity: detectionState.defectiveImages.length === 0 ? 0.5 : 1,
            cursor: detectionState.defectiveImages.length === 0 ? 'not-allowed' : 'pointer'
          }}
        >
          {detectionState.extractingROI 
            ? '🔍 Extracting ROI...' 
            : `🚀 Extract ROI from Defective Images (${detectionState.defectiveImages.length})`
          }
        </button>

        {detectionState.defectiveImages.length === 0 && (
          <p className="button-hint" style={{ fontSize: '12px', color: '#999', marginTop: '5px' }}>
            Button disabled: Please upload defective images first
          </p>
        )}

        {detectionState.extractingROI && (
          <div className="processing-info">
            <p>🔄 Extracting component regions from defective images...</p>
            <div className="progress-indicator">
              <div className="progress-bar">
                <div className="progress-fill indeterminate"></div>
              </div>
            </div>
          </div>
        )}

        {detectionState.roiResults && (
          <div className="roi-results-summary">
            <div className="success-banner">
              ✅ ROI extraction completed: {detectionState.roiResults.successful_extractions} / {detectionState.roiResults.total_images} images processed
            </div>
          </div>
        )}
      </div>

      {/* Anomaly Detection Section */}
      {detectionState.roiResults && (
        <div className="anomaly-detection-section">
          <h5>🧠 Detect Anomalies</h5>
          <p>Compare defective ROI images against your normal model to identify anomalous regions.</p>

          {/* Detection Settings */}
          <div className="detection-settings">
            <div className="settings-grid">
              <div className="setting-item">
                <label>Detection Method</label>
                <select
                  value={detectionState.method}
                  onChange={(e) => handleSettingChange('method', e.target.value)}
                  disabled={detectionState.detectingAnomalies}
                >
                  <option value="mahalanobis">Mahalanobis Distance (Recommended)</option>
                  <option value="euclidean">Euclidean Distance</option>
                  <option value="cosine">Cosine Distance</option>
                </select>
              </div>

              <div className="setting-item">
                <label>Anomaly Threshold</label>
                <div className="threshold-control">
                  <input
                    type="range"
                    value={detectionState.thresholdPercentile}
                    onChange={(e) => handleSettingChange('thresholdPercentile', parseFloat(e.target.value))}
                    min="90.0"
                    max="99.9"
                    step="0.1"
                    disabled={detectionState.detectingAnomalies}
                  />
                  <span className="threshold-value">{detectionState.thresholdPercentile}%</span>
                </div>
              </div>
            </div>
          </div>

          {errors.anomaly && (
            <div className="error-banner">
              ❌ {errors.anomaly}
            </div>
          )}

          <button
            className="detect-anomalies-button"
            onClick={detectAnomalies}
            disabled={detectionState.detectingAnomalies}
          >
            {detectionState.detectingAnomalies 
              ? '🧠 Detecting Anomalies...' 
              : '🔥 Detect Anomalies'
            }
          </button>

          {detectionState.detectingAnomalies && (
            <div className="processing-info">
              <p>🔄 Analyzing defective images against normal model...</p>
              <div className="progress-indicator">
                <div className="progress-bar">
                  <div className="progress-fill indeterminate"></div>
                </div>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Anomaly Results */}
      {detectionState.anomalyResults && (
        <div className="anomaly-results">
          <div className="results-header">
            <h5>🔥 Anomaly Detection Results</h5>
          </div>

          <div className="results-summary">
            <div className="summary-item">
              <span className="summary-label">Images Processed:</span>
              <span className="summary-value">{detectionState.anomalyResults.total_images}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Defects Found:</span>
              <span className="summary-value warning">{getAnomalyCount()}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Detection Method:</span>
              <span className="summary-value">{detectionState.anomalyResults.method}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Threshold Used:</span>
              <span className="summary-value">{detectionState.anomalyResults.threshold_percentile}%</span>
            </div>
          </div>

          {/* Heatmap Grid */}
          <div className="heatmap-section">
            <h6>🔥 Defect Heatmaps</h6>
            <div className="heatmap-grid">
              {detectionState.anomalyResults.results.slice(0, 8).map((result, index) => (
                <div key={index} className="heatmap-item">
                  <div className="heatmap-header">
                    <span className="image-name">{result.image_name.substring(13)}</span>
                    <span className={`anomaly-score ${result.image_scores.anomaly_percentage > 5.0 ? 'high' : 'low'}`}>
                      {result.image_scores.anomaly_percentage.toFixed(1)}%
                    </span>
                  </div>

                  <div className="heatmap-container">
                    <img
                      src={`data:image/png;base64,${result.score_map_base64}`}
                      alt={`Defect Heatmap ${index + 1}`}
                      className="heatmap-image"
                      title={`Max Score: ${result.image_scores.max_score.toFixed(3)}`}
                    />
                  </div>

                  <div className="heatmap-stats">
                    <div className="stat">Max: {result.image_scores.max_score.toFixed(3)}</div>
                    <div className="stat">Defective: {result.image_scores.num_anomaly_patches}</div>
                  </div>
                </div>
              ))}
            </div>

            {detectionState.anomalyResults.results.length > 8 && (
              <p className="more-results">
                ... and {detectionState.anomalyResults.results.length - 8} more images
              </p>
            )}
          </div>

          {/* Next Step Button */}
          <div className="next-step-section">
            <div className="success-banner">
              ✅ Defect detection completed! Ready to generate bounding boxes.
              <button
                className="next-step-button"
                onClick={handleProceedToNextStep}
                style={{ 
                  marginLeft: '15px', 
                  padding: '8px 16px', 
                  background: '#28a745', 
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '4px', 
                  cursor: 'pointer' 
                }}
              >
                Proceed to Stage 4 →
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Info Section */}
      <div className="step-info">
        <h5>ℹ️ About Defect Detection</h5>
        <div className="info-points">
          <div className="info-point">
            <span className="point-icon">⚠️</span>
            <span className="point-text">Upload defective images separate from normal training images</span>
          </div>
          <div className="info-point">
            <span className="point-icon">🔍</span>
            <span className="point-text">ROI extraction focuses analysis on component regions</span>
          </div>
          <div className="info-point">
            <span className="point-icon">🧠</span>
            <span className="point-text">Compares against normal model built in Stage 2</span>
          </div>
          <div className="info-point">
            <span className="point-icon">🔥</span>
            <span className="point-text">Heatmaps show anomalous regions for Stage 4</span>
          </div>
