import React, { useState } from 'react';

function Annotate() {
    const [file, setFile] = useState(null);
    const [preview, setPreview] = useState(null);
    const [annotatedImage, setAnnotatedImage] = useState(null);
    const [annotationType, setAnnotationType] = useState('detection');
    const [isLoading, setIsLoading] = useState(false);

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);
        setPreview(URL.createObjectURL(selectedFile));
        setAnnotatedImage(null); // Reset annotation when new file is selected
    };

    const handleAnnotate = async () => {
        if (!file) {
            alert("Please select a file first.");
            return;
        }

        setIsLoading(true);
        const formData = new FormData();
        formData.append("file", file);

        try {
            const endpoint = annotationType === 'detection' 
                ? "/pre-annotate-detect" 
                : "/pre-annotate-segment";

            const response = await fetch(endpoint, {
                method: "POST",
                body: formData,
            });

            if (response.ok) {
                const imageBlob = await response.blob();
                setAnnotatedImage(URL.createObjectURL(imageBlob));
            } else {
                alert(`${annotationType} annotation failed.`);
            }
        } catch (error) {
            console.error("Error during annotation:", error);
            alert("An error occurred during annotation.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div>
            <h2>Pre-annotate Image</h2>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleAnnotate}>Annotate</button>
            <div style={{ display: 'flex', marginTop: '20px' }}>
                {preview && (
                    <div style={{ marginRight: '20px' }}>
                        <h3>Original</h3>
                        <img src={preview} alt="Original" style={{ maxWidth: '400px' }} />
                    </div>
                )}
                {annotatedImage && (
                    <div>
                        <h3>Annotated</h3>
                        <img src={annotatedImage} alt="Annotated" style={{ maxWidth: '400px' }} />
                    </div>
                )}
            </div>
        </div>
    );
}

export default Annotate;
