import React, { useState } from 'react';
import axios from 'axios';

function Predict() {
  const [image, setImage] = useState(null);
  const [task, setTask] = useState('detection');
  const [result, setResult] = useState(null);
  const [message, setMessage] = useState('');

  const handlePredict = async () => {
    if (!image) {
      setMessage('Please select an image file.');
      return;
    }

    const endpoint = task === 'detection' ? '/predict-detect' : '/predict-seg';
    const formData = new FormData();
    formData.append('file', image);

    try {
      const response = await axios.post(endpoint, formData, {
        responseType: 'blob',
      });
      const imageUrl = URL.createObjectURL(response.data);
      setResult(imageUrl);
      setMessage('');
    } catch (error) {
      setMessage('Error making prediction.');
      console.error(error);
    }
  };

  return (
    <div className="section">
      <h2>Predict with a Model</h2>
      <div>
        <label>Image: </label>
        <input type="file" onChange={(e) => setImage(e.target.files[0])} />
      </div>
      <div>
        <label>Model: </label>
        <select value={task} onChange={(e) => setTask(e.target.value)}>
          <option value="detection">Object Detection</option>
          <option value="segmentation">Image Segmentation</option>
        </select>
      </div>
      <button onClick={handlePredict}>Run Prediction</button>
      {message && <p>{message}</p>}
      {result && (
        <div className="result">
          <h3>Prediction Result</h3>
          <img src={result} alt="Prediction result" />
        </div>
      )}
    </div>
  );
}

export default Predict;
