import React, { useState } from 'react';
import ROIExtractionStep from './ROIExtractionStep';
import AnomalyDetectionStep from './AnomalyDetectionStep';
import './AnomalyDetectionWorkflow.css';

function AnomalyDetectionWorkflow({ selectedProject, onBack }) {
  const [currentStep, setCurrentStep] = useState(1);
  const [stepData, setStepData] = useState({
    step1: null, // Normal ROI extraction results
    step2: null, // Normal model building results
    step3: null, // Defective images anomaly detection results
    step4: null  // Bounding box generation results
  });

  const steps = [
    {
      id: 1,
      title: "Extract Normal ROI",
      description: "Process normal images to build baseline",
      status: currentStep >= 1 ? (currentStep > 1 ? 'completed' : 'active') : 'pending'
    },
    {
      id: 2,
      title: "Build Normal Model",
      description: "Create statistical model of normal patterns",
      status: currentStep >= 2 ? (currentStep > 2 ? 'completed' : 'active') : 'pending'
    },
    {
      id: 3,
      title: "Detect Defects",
      description: "Analyze defective images for anomalies",
      status: currentStep >= 3 ? (currentStep > 3 ? 'completed' : 'active') : 'pending'
    },
    {
      id: 4,
      title: "Generate Boxes",
      description: "Convert anomalies to YOLO annotations",
      status: currentStep >= 4 ? 'active' : 'pending'
    }
  ];

  const getStepIcon = (status) => {
    switch (status) {
      case 'completed': return '✅';
      case 'active': return '🔄';
      case 'pending': return '⏳';
      default: return '⚪';
    }
  };

  const handleStepComplete = (step, data) => {
    console.log(`✅ Step ${step} completed:`, data);

    setStepData(prev => ({
      ...prev,
      [`step${step}`]: data
    }));

    // Move to next step
    setCurrentStep(step + 1);
  };

  const canProceedToStep = (stepNumber) => {
    if (stepNumber === 1) return true;
    if (stepNumber === 2) {
      // Allow Step 2 if Step 1 data exists OR if ROI extraction was previously completed
      return stepData.step1 !== null || checkROIExtractionCompleted();
    }
    if (stepNumber === 3) return stepData.step2 !== null;
    if (stepNumber === 4) return stepData.step3 !== null;
    return false;
  };

  const checkROIExtractionCompleted = () => {
    // This could be enhanced to check the backend for ROI cache existence
    // For now, we'll allow proceeding to Step 2 if user wants to retry
    return true; // Always allow access to Step 2 for now
  };

  const renderCurrentStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <ROIExtractionStep
            projectId={selectedProject?.project_id}
            projectName={selectedProject?.project_name}
            trainingImagesCount={selectedProject?.training_images_count || 0}
            onStepComplete={(data) => handleStepComplete(1, data)}
          />
        );
      case 2:
        return (
          <div className="step-placeholder">
            <h4>🧠 Step 2: Build Normal Model</h4>
            <p>Create statistical baseline from normal ROI images using DINOv2 features.</p>
            <div className="workflow-info" style={{ marginTop: '20px', textAlign: 'left' }}>
              <h5>🔧 What This Does:</h5>
              <div className="info-points">
                <div className="info-point">
                  <span className="point-icon">🔍</span>
                  <span className="point-text">Extract DINOv2 features from normal ROI images</span>
                </div>
                <div className="info-point">
                  <span className="point-icon">📊</span>
                  <span className="point-text">Build statistical model (mean, covariance) of normal patterns</span>
                </div>
                <div className="info-point">
                  <span className="point-icon">💾</span>
                  <span className="point-text">Cache the normal model for anomaly detection</span>
                </div>
              </div>
            </div>
          </div>
        );
      case 3:
        return (
          <div className="step-placeholder">
            <h4>🎯 Step 3: Detect Defects</h4>
            <p>Upload defective images and detect anomalous regions using the normal model.</p>
            <div className="workflow-info" style={{ marginTop: '20px', textAlign: 'left' }}>
              <h5>📸 Required:</h5>
              <div className="info-points">
                <div className="info-point">
                  <span className="point-icon">⚠️</span>
                  <span className="point-text">Upload defective/test images (separate from normal images)</span>
                </div>
                <div className="info-point">
                  <span className="point-icon">🔍</span>
                  <span className="point-text">Extract ROI from defective images</span>
                </div>
                <div className="info-point">
                  <span className="point-icon">🔥</span>
                  <span className="point-text">Generate anomaly heatmaps showing defective regions</span>
                </div>
              </div>
            </div>
          </div>
        );
      case 4:
        return (
          <div className="step-placeholder">
            <h4>📦 Step 4: Generate Bounding Boxes</h4>
            <p>Convert anomaly heatmaps into YOLO format bounding box annotations.</p>
            <div className="workflow-info" style={{ marginTop: '20px', textAlign: 'left' }}>
              <h5>🎯 Output:</h5>
              <div className="info-points">
                <div className="info-point">
                  <span className="point-icon">📐</span>
                  <span className="point-text">Convert high-anomaly regions to bounding boxes</span>
                </div>
                <div className="info-point">
                  <span className="point-icon">📋</span>
                  <span className="point-text">Generate YOLO format annotations (.txt files)</span>
                </div>
                <div className="info-point">
                  <span className="point-icon">🤖</span>
                  <span className="point-text">Ready for YOLO model training!</span>
                </div>
              </div>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="anomaly-detection-workflow">
      {/* Header */}
      <div className="workflow-header">
        <button className="back-button" onClick={onBack}>
          ← Back to Project
        </button>
        <div className="workflow-title">
          <h2>🧠 Anomaly Detection Workflow</h2>
          <p>Project: <strong>{selectedProject?.project_name}</strong></p>
        </div>
      </div>

      {/* Workflow Steps Progress */}
      <div className="workflow-progress">
        <div className="steps-container">
          {steps.map((step, index) => (
            <div 
              key={step.id} 
              className={`step-item ${step.status} ${canProceedToStep(step.id) ? 'clickable' : ''}`}
              onClick={() => canProceedToStep(step.id) && setCurrentStep(step.id)}
            >
              <div className="step-number">
                {getStepIcon(step.status)} {step.id}
              </div>
              <div className="step-content">
                <h4 className="step-title">{step.title}</h4>
                <p className="step-description">{step.description}</p>
              </div>
              {index < steps.length - 1 && <div className="step-connector" />}
            </div>
          ))}
        </div>

        {/* Quick Navigation */}
        {currentStep === 1 && (
          <div className="quick-navigation">
            <p className="nav-helper">💡 Already completed ROI extraction?</p>
            <button 
              className="skip-button"
              onClick={() => setCurrentStep(2)}
            >
              Skip to Step 2: Anomaly Detection →
            </button>
          </div>
        )}
      </div>

      {/* Current Step Content */}
      <div className="current-step-content">
        {renderCurrentStep()}
      </div>

      {/* Workflow Info */}
      <div className="workflow-info">
        <h5>🎯 3-Stage Anomaly Detection Workflow</h5>
        <div className="info-comparison">
          <div className="approach-comparison">
            <div className="approach-item" style={{ background: '#f0f8ff', borderLeft: '4px solid #007bff' }}>
              <h6>🔄 How It Works</h6>
              <ul>
                <li><strong>Stage 1:</strong> Extract component regions (ROI) from your images</li>
                <li><strong>Stage 2:</strong> Analyze patterns with DINOv2 and generate anomaly heatmaps</li>
                <li><strong>Stage 3:</strong> Convert heatmaps to bounding boxes for human review</li>
              </ul>
            </div>
            <div className="approach-item anomaly">
              <h6>✅ Key Advantages</h6>
              <ul>
                <li>Uses abundant normal images (no manual labeling needed)</li>
                <li>AI generates annotation candidates automatically</li>
                <li>Human just reviews/corrects AI suggestions</li>
                <li>Works well with limited labeled data</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AnomalyDetectionWorkflow;
