import React, { useState } from 'react';

function Annotate() {
    const [files, setFiles] = useState([]);
    const [previews, setPreviews] = useState([]);
    const [annotatedResults, setAnnotatedResults] = useState([]);
    const [annotationType, setAnnotationType] = useState('detection');
    const [isLoading, setIsLoading] = useState(false);
    const [processingProgress, setProcessingProgress] = useState({ current: 0, total: 0 });

    const handleFileChange = (e) => {
        const selectedFiles = Array.from(e.target.files);
        setFiles(selectedFiles);

        // Create previews for all selected files
        const newPreviews = selectedFiles.map(file => ({
            file,
            url: URL.createObjectURL(file),
            name: file.name
        }));
        setPreviews(newPreviews);
        setAnnotatedResults([]); // Reset results when new files are selected
    };

    const handleAnnotate = async () => {
        if (files.length === 0) {
            alert("Please select at least one file first.");
            return;
        }

        setIsLoading(true);
        setProcessingProgress({ current: 0, total: files.length });
        setAnnotatedResults([]);

        const endpoint = annotationType === 'detection' 
            ? "/pre-annotate-detect" 
            : "/pre-annotate-segment";

        const results = [];

        try {
            for (let i = 0; i < files.length; i++) {
                const file = files[i];
                setProcessingProgress({ current: i + 1, total: files.length });

                const formData = new FormData();
                formData.append("file", file);

                try {
                    const response = await fetch(endpoint, {
                        method: "POST",
                        body: formData,
                    });

                    if (response.ok) {
                        const imageBlob = await response.blob();
                        results.push({
                            originalName: file.name,
                            annotatedUrl: URL.createObjectURL(imageBlob),
                            success: true
                        });
                    } else {
                        results.push({
                            originalName: file.name,
                            success: false,
                            error: `${annotationType} annotation failed`
                        });
                    }
                } catch (error) {
                    console.error(`Error processing ${file.name}:`, error);
                    results.push({
                        originalName: file.name,
                        success: false,
                        error: "Processing error"
                    });
                }
            }

            setAnnotatedResults(results);
        } catch (error) {
            console.error("Error during batch annotation:", error);
            alert("An error occurred during batch annotation.");
        } finally {
            setIsLoading(false);
            setProcessingProgress({ current: 0, total: 0 });
        }
    };

    const handleDownloadZip = async () => {
        const successfulResults = annotatedResults.filter(result => result.success);

        if (successfulResults.length === 0) {
            alert("No successfully annotated images to download.");
            return;
        }

        try {
            const formData = new FormData();
            formData.append('annotationType', annotationType);

            // Add all original files that were successfully processed
            for (let result of successfulResults) {
                const originalFile = files.find(f => f.name === result.originalName);
                if (originalFile) {
                    formData.append('files', originalFile);
                }
            }

            const response = await fetch('/batch-annotate-zip', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const blob = await response.blob();
                const url = window.URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.href = url;
                a.download = `annotated_${annotationType}_batch.zip`;
                document.body.appendChild(a);
                a.click();
                window.URL.revokeObjectURL(url);
                document.body.removeChild(a);
            } else {
                alert("Failed to create zip file.");
            }
        } catch (error) {
            console.error("Error downloading zip:", error);
            alert("An error occurred while creating the zip file.");
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Pre-annotate Image</h2>

            {/* File Upload */}
            <div style={{ marginBottom: '20px' }}>
                <input 
                    type="file" 
                    onChange={handleFileChange}
                    accept="image/*"
                    style={{ marginBottom: '10px', display: 'block' }}
                />
            </div>

            {/* Annotation Type Selection */}
            <div style={{ marginBottom: '20px' }}>
                <h4>Select Annotation Type:</h4>
                <div style={{ display: 'flex', gap: '15px', alignItems: 'center' }}>
                    <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }}>
                        <input
                            type="radio"
                            value="detection"
                            checked={annotationType === 'detection'}
                            onChange={(e) => setAnnotationType(e.target.value)}
                            style={{ marginRight: '8px' }}
                        />
                        <span>üéØ <strong>Object Detection</strong> - Draw rectangles around metallic objects</span>
                    </label>
                    <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }}>
                        <input
                            type="radio"
                            value="segmentation"
                            checked={annotationType === 'segmentation'}
                            onChange={(e) => setAnnotationType(e.target.value)}
                            style={{ marginRight: '8px' }}
                        />
                        <span>‚úÇÔ∏è <strong>Segmentation</strong> - Draw exact contours around objects</span>
                    </label>
                </div>
            </div>

            {/* Annotate Button */}
            <button 
                onClick={handleAnnotate}
                disabled={!file || isLoading}
                style={{
                    backgroundColor: !file || isLoading ? '#ccc' : '#1e3a8a',
                    color: 'white',
                    padding: '12px 24px',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: !file || isLoading ? 'not-allowed' : 'pointer',
                    fontSize: '16px',
                    marginBottom: '30px'
                }}
            >
                {isLoading ? `Processing ${annotationType}...` : `Start ${annotationType}`}
            </button>

            {/* Results Display */}
            <div style={{ display: 'flex', gap: '30px', flexWrap: 'wrap' }}>
                {preview && (
                    <div style={{ flex: '1', minWidth: '300px' }}>
                        <h3>üì∑ Original Image</h3>
                        <img 
                            src={preview} 
                            alt="Original" 
                            style={{ 
                                maxWidth: '100%', 
                                height: 'auto',
                                border: '2px solid #ddd',
                                borderRadius: '8px'
                            }} 
                        />
                    </div>
                )}
                {annotatedImage && (
                    <div style={{ flex: '1', minWidth: '300px' }}>
                        <h3>
                            {annotationType === 'detection' ? 'üéØ Detection Result' : '‚úÇÔ∏è Segmentation Result'}
                        </h3>
                        <img 
                            src={annotatedImage} 
                            alt="Annotated" 
                            style={{ 
                                maxWidth: '100%', 
                                height: 'auto',
                                border: '2px solid #1e3a8a',
                                borderRadius: '8px'
                            }} 
                        />
                        <div style={{ marginTop: '10px', fontSize: '14px', color: '#666' }}>
                            {annotationType === 'detection' 
                                ? 'Bounding boxes show detected metallic objects' 
                                : 'Colored regions show segmented object areas'}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Annotate;
