import React, { useState } from 'react';
import ROIExtractionStep from './ROIExtractionStep';
import AnomalyDetectionStep from './AnomalyDetectionStep';
import './AnomalyDetectionWorkflow.css';

function AnomalyDetectionWorkflow({ selectedProject, onBack }) {
  const [currentStep, setCurrentStep] = useState(1);
  const [stepData, setStepData] = useState({
    step1: null, // ROI extraction results
    step2: null, // Anomaly detection results
    step3: null, // Annotation candidates
    step4: null  // Human review results
  });

  const steps = [
    {
      id: 1,
      title: "Extract ROI",
      description: "Identify and crop component regions",
      status: currentStep >= 1 ? (currentStep > 1 ? 'completed' : 'active') : 'pending'
    },
    {
      id: 2,
      title: "Anomaly Detection",
      description: "Generate anomaly heatmaps using DINOv2",
      status: currentStep >= 2 ? (currentStep > 2 ? 'completed' : 'active') : 'pending'
    },
    {
      id: 3,
      title: "Generate Annotations",
      description: "Convert heatmaps to bounding box candidates",
      status: currentStep >= 3 ? 'active' : 'pending'
    }
  ];

  const getStepIcon = (status) => {
    switch (status) {
      case 'completed': return '✅';
      case 'active': return '🔄';
      case 'pending': return '⏳';
      default: return '⚪';
    }
  };

  const handleStepComplete = (step, data) => {
    console.log(`✅ Step ${step} completed:`, data);

    setStepData(prev => ({
      ...prev,
      [`step${step}`]: data
    }));

    // Move to next step
    setCurrentStep(step + 1);
  };

  const canProceedToStep = (stepNumber) => {
    if (stepNumber === 1) return true;
    if (stepNumber === 2) {
      // Allow Step 2 if Step 1 data exists OR if ROI extraction was previously completed
      return stepData.step1 !== null || checkROIExtractionCompleted();
    }
    if (stepNumber === 3) return stepData.step2 !== null;
    if (stepNumber === 4) return stepData.step3 !== null;
    return false;
  };

  const checkROIExtractionCompleted = () => {
    // This could be enhanced to check the backend for ROI cache existence
    // For now, we'll allow proceeding to Step 2 if user wants to retry
    return true; // Always allow access to Step 2 for now
  };

  const renderCurrentStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <ROIExtractionStep
            projectId={selectedProject?.project_id}
            projectName={selectedProject?.project_name}
            trainingImagesCount={selectedProject?.training_images_count || 0}
            onStepComplete={(data) => handleStepComplete(1, data)}
          />
        );
      case 2:
        return (
          <AnomalyDetectionStep
            projectId={selectedProject?.project_id}
            roiData={stepData.step1}
            onStepComplete={(data) => handleStepComplete(2, data)}
          />
        );
      case 3:
        return (
          <div className="step-placeholder">
            <h4>📦 Step 3: Generate Annotation Candidates</h4>
            <p>Coming soon: Convert anomaly heatmaps to bounding boxes</p>
          </div>
        );
      case 4:
        return (
          <div className="step-placeholder">
            <h4>👨‍💻 Step 4: Human-in-the-Loop Review</h4>
            <p>Coming soon: Review and correct generated annotations</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="anomaly-detection-workflow">
      {/* Header */}
      <div className="workflow-header">
        <button className="back-button" onClick={onBack}>
          ← Back to Project
        </button>
        <div className="workflow-title">
          <h2>🧠 Anomaly Detection Workflow</h2>
          <p>Project: <strong>{selectedProject?.project_name}</strong></p>
        </div>
      </div>

      {/* Workflow Steps Progress */}
      <div className="workflow-progress">
        <div className="steps-container">
          {steps.map((step, index) => (
            <div 
              key={step.id} 
              className={`step-item ${step.status} ${canProceedToStep(step.id) ? 'clickable' : ''}`}
              onClick={() => canProceedToStep(step.id) && setCurrentStep(step.id)}
            >
              <div className="step-number">
                {getStepIcon(step.status)} {step.id}
              </div>
              <div className="step-content">
                <h4 className="step-title">{step.title}</h4>
                <p className="step-description">{step.description}</p>
              </div>
              {index < steps.length - 1 && <div className="step-connector" />}
            </div>
          ))}
        </div>

        {/* Quick Navigation */}
        {currentStep === 1 && (
          <div className="quick-navigation">
            <p className="nav-helper">💡 Already completed ROI extraction?</p>
            <button 
              className="skip-button"
              onClick={() => setCurrentStep(2)}
            >
              Skip to Step 2: Anomaly Detection →
            </button>
          </div>
        )}
      </div>

      {/* Current Step Content */}
      <div className="current-step-content">
        {renderCurrentStep()}
      </div>

      {/* Workflow Info */}
      <div className="workflow-info">
        <h5>🎯 Why Anomaly Detection?</h5>
        <div className="info-comparison">
          <div className="approach-comparison">
            <div className="approach-item traditional">
              <h6>❌ Traditional Approach Issues</h6>
              <ul>
                <li>Requires many labeled defect examples</li>
                <li>Manual annotation is time-consuming</li>
                <li>Human labeling errors affect quality</li>
                <li>Poor results with limited data (30 images)</li>
              </ul>
            </div>
            <div className="approach-item anomaly">
              <h6>✅ Anomaly Detection Benefits</h6>
              <ul>
                <li>Uses abundant normal images only</li>
                <li>AI suggests annotation candidates</li>
                <li>Human just reviews/corrects suggestions</li>
                <li>Works well with limited labeled data</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AnomalyDetectionWorkflow;
