import React, { useState } from 'react';

function Annotate() {
    const [files, setFiles] = useState([]);
    const [previews, setPreviews] = useState([]);
    const [annotatedResults, setAnnotatedResults] = useState([]);
    const [annotationType, setAnnotationType] = useState('detection');
    const [isLoading, setIsLoading] = useState(false);
    const [processingProgress, setProcessingProgress] = useState({ current: 0, total: 0 });

    const handleFileChange = (e) => {
        const selectedFiles = Array.from(e.target.files);
        setFiles(selectedFiles);

        // Create previews for all selected files
        const newPreviews = selectedFiles.map(file => ({
            file,
            url: URL.createObjectURL(file),
            name: file.name
        }));
        setPreviews(newPreviews);
        setAnnotatedResults([]); // Reset results when new files are selected
    };

    const handleAnnotate = async () => {
        if (files.length === 0) {
            alert("Please select at least one file first.");
            return;
        }

        setIsLoading(true);
        setProcessingProgress({ current: 0, total: files.length });
        setAnnotatedResults([]);

        const endpointMap = {
            'detection': '/pre-annotate-detect',
            'segmentation': '/pre-annotate-segment',
            'sam2-detection': '/pre-annotate-sam2-detect',
            'sam2-segmentation': '/pre-annotate-sam2-segment'
        };
        const endpoint = endpointMap[annotationType] || '/pre-annotate-detect';

        const results = [];

        try {
            for (let i = 0; i < files.length; i++) {
                const file = files[i];
                setProcessingProgress({ current: i + 1, total: files.length });

                const formData = new FormData();
                formData.append("file", file);

                try {
                    const response = await fetch(endpoint, {
                        method: "POST",
                        body: formData,
                    });

                    if (response.ok) {
                        const imageBlob = await response.blob();
                        results.push({
                            originalName: file.name,
                            annotatedUrl: URL.createObjectURL(imageBlob),
                            success: true
                        });
                    } else {
                        results.push({
                            originalName: file.name,
                            success: false,
                            error: `${annotationType} annotation failed`
                        });
                    }
                } catch (error) {
                    console.error(`Error processing ${file.name}:`, error);
                    results.push({
                        originalName: file.name,
                        success: false,
                        error: "Processing error"
                    });
                }
            }

            setAnnotatedResults(results);
        } catch (error) {
            console.error("Error during batch annotation:", error);
            alert("An error occurred during batch annotation.");
        } finally {
            setIsLoading(false);
            setProcessingProgress({ current: 0, total: 0 });
        }
    };

    const handleDownloadZip = async () => {
        const successfulResults = annotatedResults.filter(result => result.success);

        if (successfulResults.length === 0) {
            alert("No successfully annotated images to download.");
            return;
        }

        try {
            const formData = new FormData();
            formData.append('annotationType', annotationType);

            // Add all original files that were successfully processed
            for (let result of successfulResults) {
                const originalFile = files.find(f => f.name === result.originalName);
                if (originalFile) {
                    formData.append('files', originalFile);
                }
            }

            const response = await fetch('/batch-annotate-zip', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const blob = await response.blob();
                const url = window.URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.href = url;
                a.download = `annotated_${annotationType}_batch.zip`;
                document.body.appendChild(a);
                a.click();
                window.URL.revokeObjectURL(url);
                document.body.removeChild(a);
            } else {
                alert("Failed to create zip file.");
            }
        } catch (error) {
            console.error("Error downloading zip:", error);
            alert("An error occurred while creating the zip file.");
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Pre-annotate Images</h2>

            {/* File Upload */}
            <div style={{ marginBottom: '20px' }}>
                <input 
                    type="file" 
                    multiple
                    onChange={handleFileChange}
                    accept="image/*"
                    style={{ marginBottom: '10px', display: 'block' }}
                />
                {files.length > 0 && (
                    <div style={{ marginTop: '10px', fontSize: '14px', color: '#666' }}>
                        üìÅ {files.length} file{files.length > 1 ? 's' : ''} selected
                    </div>
                )}
            </div>

            {/* Preview Selected Images */}
            {previews.length > 0 && (
                <div style={{ marginBottom: '20px' }}>
                    <h4>Selected Images:</h4>
                    <div style={{ 
                        display: 'grid', 
                        gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', 
                        gap: '15px',
                        marginBottom: '20px'
                    }}>
                        {previews.map((preview, index) => (
                            <div key={index} style={{ textAlign: 'center' }}>
                                <img 
                                    src={preview.url} 
                                    alt={preview.name}
                                    style={{ 
                                        width: '100%', 
                                        height: '120px', 
                                        objectFit: 'cover',
                                        border: '2px solid #ddd',
                                        borderRadius: '8px'
                                    }}
                                />
                                <div style={{ fontSize: '12px', marginTop: '5px', wordBreak: 'break-all' }}>
                                    {preview.name}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            {/* Annotation Type Selection */}
            <div style={{ marginBottom: '20px' }}>
                <h4>Select Annotation Method:</h4>

                {/* OpenCV Methods */}
                <div style={{ marginBottom: '15px' }}>
                    <h5 style={{ margin: '5px 0', color: '#666' }}>üîß OpenCV (Traditional)</h5>
                    <div style={{ display: 'flex', gap: '15px', alignItems: 'center', flexWrap: 'wrap', marginLeft: '20px' }}>
                        <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }}>
                            <input
                                type="radio"
                                value="detection"
                                checked={annotationType === 'detection'}
                                onChange={(e) => setAnnotationType(e.target.value)}
                                style={{ marginRight: '8px' }}
                            />
                            <span>üéØ <strong>Detection</strong> - Basic edge detection with bounding boxes</span>
                        </label>
                        <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }}>
                            <input
                                type="radio"
                                value="segmentation"
                                checked={annotationType === 'segmentation'}
                                onChange={(e) => setAnnotationType(e.target.value)}
                                style={{ marginRight: '8px' }}
                            />
                            <span>‚úÇÔ∏è <strong>Segmentation</strong> - Basic contour detection</span>
                        </label>
                    </div>
                </div>

                {/* SAM2 Methods */}
                <div style={{ marginBottom: '15px' }}>
                    <h5 style={{ margin: '5px 0', color: '#059669' }}>ü§ñ SAM2 (AI-Powered) - Recommended</h5>
                    <div style={{ display: 'flex', gap: '15px', alignItems: 'center', flexWrap: 'wrap', marginLeft: '20px' }}>
                        <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }}>
                            <input
                                type="radio"
                                value="sam2-detection"
                                checked={annotationType === 'sam2-detection'}
                                onChange={(e) => setAnnotationType(e.target.value)}
                                style={{ marginRight: '8px' }}
                            />
                            <span>üéØ <strong>SAM2 Detection</strong> - Single main object detection (best for primary metallic parts)</span>
                        </label>
                        <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }}>
                            <input
                                type="radio"
                                value="sam2-segmentation"
                                checked={annotationType === 'sam2-segmentation'}
                                onChange={(e) => setAnnotationType(e.target.value)}
                                style={{ marginRight: '8px' }}
                            />
                            <span>‚úÇÔ∏è <strong>SAM2 Segmentation</strong> - Precise AI masks (‚ö†Ô∏è Slow on CPU: 30-60s/image)</span>
                        </label>
                    </div>
                </div>

                {/* Info Box */}
                <div style={{ 
                    backgroundColor: annotationType.startsWith('sam2') ? '#f0fdf4' : '#f8fafc', 
                    border: `1px solid ${annotationType.startsWith('sam2') ? '#059669' : '#e2e8f0'}`,
                    borderRadius: '6px',
                    padding: '10px',
                    fontSize: '14px',
                    marginTop: '10px'
                }}>
                    {annotationType.startsWith('sam2') ? (
                        <div>
                            <strong>üöÄ SAM2 Benefits:</strong> Better accuracy with metallic objects, works with any lighting/background, 
                            AI-powered precision. First run will download the model (~40MB for CPU-optimized version).
                            <br />
                            <strong>‚ö†Ô∏è Performance:</strong> GPU: ~2-5 sec/image | CPU: ~10-30 sec/image (auto-optimized)
                        </div>
                    ) : (
                        <div>
                            <strong>‚ö° OpenCV:</strong> Very fast (~0.5-2 sec/image) but may struggle with complex backgrounds or lighting variations.
                        </div>
                    )}
                </div>
            </div>

            {/* Processing Controls */}
            <div style={{ marginBottom: '30px' }}>
                <button 
                    onClick={handleAnnotate}
                    disabled={files.length === 0 || isLoading}
                    style={{
                        backgroundColor: files.length === 0 || isLoading ? '#ccc' : '#1e3a8a',
                        color: 'white',
                        padding: '12px 24px',
                        border: 'none',
                        borderRadius: '6px',
                        cursor: files.length === 0 || isLoading ? 'not-allowed' : 'pointer',
                        fontSize: '16px',
                        marginRight: '15px'
                    }}
                >
                    {isLoading ? `Processing ${annotationType}...` : `Start Batch ${annotationType}`}
                </button>

                {annotatedResults.length > 0 && (
                    <button 
                        onClick={handleDownloadZip}
                        style={{
                            backgroundColor: '#059669',
                            color: 'white',
                            padding: '12px 24px',
                            border: 'none',
                            borderRadius: '6px',
                            cursor: 'pointer',
                            fontSize: '16px'
                        }}
                    >
                        üì¶ Download Zip ({annotatedResults.filter(r => r.success).length} files)
                    </button>
                )}
            </div>

            {/* Progress Bar */}
            {isLoading && (
                <div style={{ marginBottom: '20px' }}>
                    <div style={{ fontSize: '14px', marginBottom: '8px' }}>
                        Processing: {processingProgress.current} / {processingProgress.total}
                    </div>
                    <div style={{ 
                        width: '100%', 
                        backgroundColor: '#f0f0f0', 
                        borderRadius: '8px',
                        height: '8px'
                    }}>
                        <div style={{ 
                            width: `${(processingProgress.current / processingProgress.total) * 100}%`,
                            backgroundColor: '#1e3a8a',
                            height: '8px',
                            borderRadius: '8px',
                            transition: 'width 0.3s ease'
                        }} />
                    </div>
                </div>
            )}

            {/* Results Display */}
            {annotatedResults.length > 0 && (
                <div>
                    <h3>üìã Processing Results</h3>
                    <div style={{ 
                        display: 'grid', 
                        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
                        gap: '20px' 
                    }}>
                        {annotatedResults.map((result, index) => (
                            <div key={index} style={{ 
                                border: `2px solid ${result.success ? '#059669' : '#dc2626'}`,
                                borderRadius: '8px',
                                padding: '15px'
                            }}>
                                <h4 style={{ 
                                    margin: '0 0 10px 0',
                                    color: result.success ? '#059669' : '#dc2626'
                                }}>
                                    {result.success ? '‚úÖ' : '‚ùå'} {result.originalName}
                                </h4>

                                {result.success ? (
                                    <div>
                                        <img 
                                            src={result.annotatedUrl} 
                                            alt={`Annotated ${result.originalName}`}
                                            style={{ 
                                                width: '100%', 
                                                height: 'auto',
                                                borderRadius: '6px'
                                            }}
                                        />
                                        <div style={{ fontSize: '12px', marginTop: '5px', color: '#666' }}>
                                            {annotationType === 'detection' && 'Objects detected with OpenCV bounding boxes'}
                                            {annotationType === 'segmentation' && 'Objects segmented with OpenCV contours'}
                                            {annotationType === 'sam2-detection' && 'Objects detected with SAM2 AI bounding boxes'}
                                            {annotationType === 'sam2-segmentation' && 'Objects segmented with SAM2 AI precision masks'}
                                        </div>
                                    </div>
                                ) : (
                                    <div style={{ color: '#dc2626', fontSize: '14px' }}>
                                        Error: {result.error}
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
}

export default Annotate;
